    { typesEnv = Map.fromList transData.typeAliases
    , termsEnv = Map.fromList $ transData.functions
      <&> \(name, (ftd, _)) -> (name, ftd)
    , exceptionType = transData.exceptionType
    , extensions = mempty
    , universalTypeVars = mempty -- TODO: check
