      then do
        desiredFtd <- case funcType of
          FuncType ftd -- useful?
            | length ftd.argsType /= length args
            -> failWith x (ErrorIncorrectNumberOfArguments $ length args)
            | otherwise -> pure (Just ftd)
          _ -> pure Nothing
        funcRetTypeVar <- Env.newTypeVar
